import React, { useState, useEffect } from "react";
import { jwtDecode } from "jwt-decode";
import { Container } from "react-bootstrap";
import CustomNavbar from "./Navbar.jsx";
import Authentication from "./Authentication.jsx";
import Profile from "./Profile.jsx";
import Products from "./Products.jsx";
import Home from "./Home.jsx";

function App() {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [currentPage, setCurrentPage] = useState("home"); // Track the current page

  useEffect(() => {
    if (token) {
      try {
        const decodedToken = jwtDecode(token);

        if (decodedToken.exp * 1000 < Date.now()) {
          console.warn("Token has expired");
          handleLogout();
        } else {
          setUser({ id: decodedToken.id, username: decodedToken.username });
        }
      } catch (error) {
        console.error("Invalid token:", error);
        handleLogout();
      }
    }
  }, [token]);

  const handleLogin = (user, token) => {
    setUser(user);
    setToken(token);
    localStorage.setItem("token", token);
    setCurrentPage("profile"); // Navigate to Profile after login
  };

  const handleLogout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem("token");
    setCurrentPage("home"); // Navigate to Home after logout
  };

  const renderPage = () => {
    if (!user) {
      if (currentPage === "home") {
        return <Home onShowAuth={() => setCurrentPage("auth")} />;
      } else if (currentPage === "auth") {
        return <Authentication onLogin={handleLogin} />;
      }
    } else {
      return (
        <div>
          <h1 className="mb-4 text-center">Welcome, {user.username}</h1>
          <Profile token={token} />
          <Products token={token} />
        </div>
      );
    }
  };

  return (
    <div>
      <CustomNavbar
        user={user}
        onLoginClick={() => setCurrentPage("auth")}
        onLogout={handleLogout}
        onNavigateHome={() => setCurrentPage("home")}
        onNavigateProfile={() => setCurrentPage("profile")}
      />
      <Container className="mt-5">{renderPage()}</Container>
    </div>
  );
}

export default App;
