import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from "./AuthProvider.jsx";
import { Form, Button, Container, Row, Col, Table, Alert } from "react-bootstrap";

function Seller() {
  const { token } = useContext(AuthContext);
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/products", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const data = await response.json();
        if (response.ok) {
          setProducts(data);
        } else {
          setError(data.message);
        }
      } catch (err) {
        setError("Error fetching products.");
      }
    };
    fetchProducts();
  }, [token]);

  const handleAddToCart = (product, quantity) => {
    if (quantity <= 0) {
      setError("Quantity must be greater than 0.");
      return;
    }
    const existingItem = cart.find((item) => item.id === product.id);
    if (existingItem) {
      setCart(
        cart.map((item) =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + quantity }
            : item
        )
      );
    } else {
      setCart([...cart, { ...product, quantity }]);
    }
    setError("");
  };

  const handleRemoveFromCart = (productId) => {
    setCart(cart.filter((item) => item.id !== productId));
  };

  const handleCheckout = async () => {
    if (cart.length === 0) {
      setError("Cart is empty.");
      return;
    }
    if (!paymentMethod) {
      setError("Please select a payment method.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/receipts", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          items: cart.map(({ id, quantity }) => ({ id, quantity })),
          payment_method: paymentMethod,
        }),
      });
      const data = await response.json();
      if (response.ok) {
        setSuccess("Transaction completed successfully!");
        setCart([]);
        setPaymentMethod("");
      } else {
        setError(data.message);
      }
    } catch (err) {
      setError("Error completing transaction.");
    }
  };

  return (
    <Container className="mt-5">
      <h1 className="text-center mb-4">Sales Terminal</h1>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      <Row>
        <Col md={6}>
          <h3>Products</h3>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Name</th>
                <th>Price</th>
                <th>Stock</th>
                <th>Quantity</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product) => (
                <tr key={product.id}>
                  <td>{product.displayname}</td>
                  <td>${product.price}</td>
                  <td>{product.stock}</td>
                  <td>
                    <Form.Control
                      type="number"
                      min="1"
                      max={product.stock}
                      onChange={(e) =>
                        (product.quantity = parseInt(e.target.value, 10) || 0)
                      }
                    />
                  </td>
                  <td>
                    <Button
                      variant="primary"
                      onClick={() =>
                        handleAddToCart(product, product.quantity || 0)
                      }
                    >
                      Add to Cart
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Col>
        <Col md={6}>
          <h3>Cart</h3>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Name</th>
                <th>Quantity</th>
                <th>Price</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {cart.map((item) => (
                <tr key={item.id}>
                  <td>{item.displayname}</td>
                  <td>{item.quantity}</td>
                  <td>${item.price * item.quantity}</td>
                  <td>
                    <Button
                      variant="danger"
                      onClick={() => handleRemoveFromCart(item.id)}
                    >
                      Remove
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
          <Form.Group className="mb-3">
            <Form.Label>Payment Method</Form.Label>
            <Form.Control
              as="select"
              value={paymentMethod}
              onChange={(e) => setPaymentMethod(e.target.value)}
            >
              <option value="">Select Payment Method</option>
              <option value="cash">Cash</option>
              <option value="credit_card">Credit Card</option>
              <option value="mobile_payment">Mobile Payment</option>
            </Form.Control>
          </Form.Group>
          <Button variant="success" onClick={handleCheckout}>
            Checkout
          </Button>
        </Col>
      </Row>
    </Container>
  );
}

export default Seller;
