import React, { useState, useEffect } from "react";
import { jwtDecode } from "jwt-decode";
import { Container } from "react-bootstrap";
import CustomNavbar from "./Navbar.jsx";
import Authentication from "./Authentication.jsx";
import Profile from "./Profile.jsx";
import Products from "./Products.jsx";

function App() {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem("token"));

  useEffect(() => {
    if (token) {
      try {
        const decodedToken = jwtDecode(token);

        if (decodedToken.exp * 1000 < Date.now()) {
          console.warn("Token has expired");
          handleLogout();
        } else {
          setUser({ id: decodedToken.id, username: decodedToken.username });
        }
      } catch (error) {
        console.error("Invalid token:", error);
        handleLogout();
      }
    }
  }, [token]);

  const handleLogin = (user, token) => {
    setUser(user);
    setToken(token);
    localStorage.setItem("token", token);
  };

  const handleLogout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem("token");
  };

  return (
    <div>
      <CustomNavbar user={user} onLoginClick={() => console.log("Login/Register clicked")} onLogout={handleLogout} />
      <Container className="mt-5">
        {!user ? (
          <Authentication onLogin={handleLogin} />
        ) : (
          <div>
            <h1 className="mb-4 text-center">Welcome, {user.username}</h1>
            <Profile token={token} />
            <Products token={token} />
          </div>
        )}
      </Container>
    </div>
  );
}

export default App;
