import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from "./AuthProvider.jsx";

function ProductsManager() {
  const { token } = useContext(AuthContext);
  const [products, setProducts] = useState([]);
  const [product, setProduct] = useState({
    id: 0,
    name: "",
    price: "",
    description: "",
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [productIds, setProductIds] = useState([]);
  const [maxProductId, setMaxProductId] = useState(0);

  const fetchProducts = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/products", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await response.json();
      if (response.ok) {
        setProducts(data);
        setProductIds(data.map((p) => ({ id: p.id, name: p.itemname })));
        setMaxProductId(data.length);
      } else {
        setError(data.message);
      }
    } catch (err) {
      setError("Error fetching products.");
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [token]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === "id") {
      const idValue = parseInt(value, 10);
      if (idValue < 0) return;
      if (idValue === 0) {
        setProduct({ id: 0, name: "", price: "", description: "" });
        return;
      }
      setProduct({
        ...product,
        [name]: idValue > maxProductId ? maxProductId : idValue,
      });
    } else {
      setProduct({ ...product, [name]: value });
    }
  };

  const fetchProductDetails = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/products/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await response.json();
      if (response.ok) {
        setProduct(data);
      } else {
        setError(data.message);
      }
    } catch (err) {
      setError("Error fetching product details.");
    }
  };

  useEffect(() => {
    if (product.id && product.id !== 0) {
      fetchProductDetails(product.id);
    }
  }, [product.id]);

  const handleAddProduct = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!product.name || !product.price || !product.description) {
      setError("All fields are required.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(product),
      });
      const data = await response.json();
      if (response.ok) {
        setSuccess("Product added successfully!");
        setProduct({ id: 0, name: "", price: "", description: "" });
        fetchProducts();
      } else {
        setError(data.message);
      }
    } catch (err) {
      setError("Error adding product.");
    }
  };

  const handleUpdateProduct = async (e) => {
    e.preventDefault();
    if (window.confirm("Are you sure you want to update this product?")) {
      try {
        const response = await fetch(`http://localhost:5000/api/products/${product.id}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(product),
        });
        const data = await response.json();
        if (response.ok) {
          setSuccess("Product updated successfully!");
          fetchProducts();
        } else {
          setError(data.message);
        }
      } catch (err) {
        setError("Error updating product.");
      }
    }
  };

  const handleDeleteProduct = async () => {
    if (!product.id) {
      setError("No product selected to delete.");
      return;
    }
    if (window.confirm("Are you sure you want to delete this product?")) {
      try {
        const response = await fetch(`http://localhost:5000/api/products/${product.id}`, {
          method: "DELETE",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const data = await response.json();
        if (response.ok) {
          setSuccess("Product deleted successfully!");
          setProduct({ id: 0, name: "", price: "", description: "" });
          fetchProducts();
        } else {
          setError(data.message);
        }
      } catch (err) {
        setError("Error deleting product.");
      }
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">Products Manager</h1>
      {error && <p className="text-danger text-center">{error}</p>}
      {success && <p className="text-success text-center">{success}</p>}

      <form onSubmit={handleAddProduct} className="mb-4">
        <h3>Add or Update Product</h3>
        <div className="form-group">
          <label htmlFor="id">Product ID</label>
          <div className="row">
            <div className="col-md-4">
              <input
                type="number"
                className="form-control"
                id="id"
                name="id"
                value={product.id}
                onChange={handleInputChange}
                max={maxProductId}
                required
              />
            </div>
            <div className="col-md-8">
              <select
                className="form-control"
                id="id"
                name="id"
                value={product.id}
                onChange={handleInputChange}
                required
              >
                <option value={0}>Select Product</option>
                {productIds.map((p) => (
                  <option key={p.id} value={p.id}>
                    {p.id} - {p.name}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>
        <div className="form-group">
          <label htmlFor="name">Product Name</label>
          <input
            type="text"
            className="form-control"
            id="name"
            name="name"
            value={product.name}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="price">Product Price</label>
          <input
            type="number"
            className="form-control"
            id="price"
            name="price"
            value={product.price}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="description">Product Description</label>
          <textarea
            className="form-control"
            id="description"
            name="description"
            value={product.description}
            onChange={handleInputChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary btn-block">
          Add Product
        </button>
        <button
          type="button"
          className="btn btn-success btn-block mt-2"
          onClick={handleUpdateProduct}
          disabled={product.id === 0}
        >
          Update Product
        </button>
        <button
          type="button"
          className="btn btn-danger btn-block mt-2"
          onClick={handleDeleteProduct}
          disabled={product.id === 0}
        >
          Delete Product
        </button>
      </form>
    </div>
  );
}

export default ProductsManager;
