import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from "./AuthProvider.jsx";
import { Card, Button, Modal } from "react-bootstrap";

function Products() {
  const { token } = useContext(AuthContext);
  const [products, setProducts] = useState([]);
  const [error, setError] = useState("");
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [showModal, setShowModal] = useState(false); 

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/products", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        const data = await response.json();
        if (response.ok) {
          setProducts(data);
        } else {
          setError(data.message);
        }
      } catch (err) {
        setError("Error during fetching products");
      }
    };
    fetchProducts();
  }, [token]);

  const handleShowModal = (product) => {
    setSelectedProduct(product);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setSelectedProduct(null);
    setShowModal(false);
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Products</h1>
      {error && <p className="text-danger text-center">{error}</p>}
      <div className="row">
        {products.map((product) => (
          <div className="col-md-4 col-sm-6 mb-4" key={product.id}>
            <Card className="h-100 shadow-sm">
              <Card.Img
                variant="top"
                src={product.image_url}
                alt={product.displayname}
                style={{ height: "200px", objectFit: "cover" }}
              />
              <Card.Body>
                <Card.Title className="text-truncate">{product.displayname}</Card.Title>
                <Card.Text>
                  {product.description.length > 50 ? (
                    <>
                      {product.description.substring(0, 50)}...{" "}
                      <Button
                        variant="link"
                        className="p-0"
                        onClick={() => handleShowModal(product)}
                      >
                        Click to expand
                      </Button>
                    </>
                  ) : (
                    product.description
                  )}
                </Card.Text>
                <Card.Text className="fw-bold">${product.price}</Card.Text>
                <Button variant="primary" className="w-100">
                  Add to Cart
                </Button>
              </Card.Body>
            </Card>
          </div>
        ))}
      </div>

      <Modal show={showModal} onHide={handleCloseModal} centered>
        {selectedProduct && (
          <>
            <Modal.Header closeButton>
              <Modal.Title>{selectedProduct.displayname}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <img
                src={selectedProduct.image_url}
                alt={selectedProduct.displayname}
                className="img-fluid mb-3"
                style={{ maxHeight: "300px", objectFit: "cover" }}
              />
              <p>{selectedProduct.description}</p>
              <p className="fw-bold">Price: ${selectedProduct.price}</p>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={handleCloseModal}>
                Close
              </Button>
            </Modal.Footer>
          </>
        )}
      </Modal>
    </div>
  );
}

export default Products;
