import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from "./AuthProvider.jsx";
import { Form, Button, Container, Row, Col, Alert, Modal, Card } from "react-bootstrap";

function ProductsManager() {
  const { token } = useContext(AuthContext);
  const [products, setProducts] = useState([]);
  const [product, setProduct] = useState({
    id: 0,
    itemname: "",
    displayname: "",
    price: "",
    stock: "",
    image_url: "",
    description: "",
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [productIds, setProductIds] = useState([]);
  const [maxProductId, setMaxProductId] = useState(0);
  const [showAddModal, setShowAddModal] = useState(false);
  const [tempImageUrl, setTempImageUrl] = useState("");

  const defaultImageUrl = "https://www.cams-it.com/wp-content/uploads/2015/05/default-placeholder-200x200.png";

  const handleResetImageUrl = () => {
    setProduct({ ...product, image_url: defaultImageUrl });
  };

  const fetchProducts = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/products", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await response.json();
      if (response.ok) {
        setProducts(data.sort((a, b) => a.id - b.id)); // Sort products by ID
        setProductIds(data.map((p) => ({ id: p.id, name: p.itemname })));
        setMaxProductId(data.length);
      } else {
        setError(data.message);
      }
    } catch (err) {
      setError("Error fetching products.");
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [token]);

  const resetProductFields = () => {
    setProduct({
      id: 0,
      itemname: "",
      displayname: "",
      price: "",
      stock: "",
      image_url: "",
      description: "",
    });
    setTempImageUrl(""); // Clear temporary image URL
    setError("");
    setSuccess("");
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === "price" || name === "stock") {
      const numericValue = Math.max(0, parseInt(value, 10) || 0); // Ensure value is at least 0
      setProduct({ ...product, [name]: numericValue });
    } else if (name === "id" && parseInt(value, 10) === 0) {
      setProduct({
        id: 0,
        itemname: "",
        displayname: "",
        price: "",
        stock: "",
        image_url: "",
        description: "",
      });
      setError("");
      setSuccess("");
    } else {
      setProduct({ ...product, [name]: value });
    }
  };

  const handleSelectProduct = (e) => {
    const selectedId = parseInt(e.target.value, 10);
    if (selectedId === 0) {
      setProduct({
        id: 0,
        itemname: "",
        displayname: "",
        price: "",
        stock: "",
        image_url: "",
        description: "",
      });
      setError("");
      setSuccess("");
    } else {
      setProduct({ ...product, id: selectedId });
    }
  };

  const fetchProductDetails = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/products/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await response.json();
      if (response.ok) {
        setProduct({
          id: data.id,
          itemname: data.itemname,
          displayname: data.displayname,
          price: data.price,
          stock: data.stock,
          image_url: data.image_url,
          description: data.description,
        });
      } else {
        setError(data.message);
      }
    } catch (err) {
      setError("Error fetching product details.");
    }
  };

  useEffect(() => {
    if (product.id && product.id !== 0) {
      fetchProductDetails(product.id);
    }
  }, [product.id]);

  const handleAddProduct = async () => {
    setError("");
    setSuccess("");

    const productToSend = {
      ...product,
      price: product.price || 0, // Default to 0 if empty
      stock: product.stock || 0, // Default to 0 if empty
      image_url: product.image_url || "https://www.cams-it.com/wp-content/uploads/2015/05/default-placeholder-200x200.png",
    };

    if (!productToSend.itemname || !productToSend.displayname || productToSend.description.trim() === "") {
      setError("All fields are required.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(productToSend),
      });
      const data = await response.json();
      if (response.ok) {
        setSuccess("Product added successfully!");
        resetProductFields();
        fetchProducts();
        setShowAddModal(false);
      } else {
        setError(data.message);
      }
    } catch (err) {
      setError("Error adding product.");
    }
  };

  const handleUpdateProduct = async () => {
    if (window.confirm("Are you sure you want to update this product?")) {
      const productToSend = {
        ...product,
        price: product.price || 0, // Default to 0 if empty
        stock: product.stock || 0, // Default to 0 if empty
      };

      if (!productToSend.itemname || !productToSend.displayname || productToSend.description.trim() === "") {
        setError("All fields are required.");
        return;
      }

      try {
        const response = await fetch(`http://localhost:5000/api/products/${product.id}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(productToSend),
        });
        const data = await response.json();
        if (response.ok) {
          setSuccess("Product updated successfully!");
          fetchProducts();
          resetProductFields(); // Reset form fields after update
        } else {
          setError(data.message);
        }
      } catch (err) {
        setError("Error updating product.");
      }
    }
  };

  const handleDeleteProduct = async () => {
    if (!product.id) {
      setError("No product selected to delete.");
      return;
    }
    if (window.confirm("Are you sure you want to delete this product?")) {
      try {
        const response = await fetch(`http://localhost:5000/api/products/${product.id}`, {
          method: "DELETE",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const data = await response.json();
        if (response.ok) {
          setSuccess("Product deleted successfully!");
          setProduct({ id: 0, itemname: "", displayname: "", price: "", stock: "", image_url: "", description: "" });
          fetchProducts();
        } else {
          setError(data.message);
        }
      } catch (err) {
        setError("Error deleting product.");
      }
    }
  };

  const handleImageUpload = () => {
    const img = new Image();
    img.src = tempImageUrl;
    img.onload = () => {
      setProduct({ ...product, image_url: tempImageUrl });
      setTempImageUrl("");
      setError("");
    };
    img.onerror = () => {
      setError("Invalid image URL.");
    };
  };

  const handleImagePreview = () => {
    const img = new Image();
    img.src = tempImageUrl;
    img.onload = () => {
      setProduct({ ...product, image_url: tempImageUrl });
      setTempImageUrl("");
      setError("");
    };
    img.onerror = () => {
      setError("Invalid image URL.");
    };
  };

  const handleAddProductClick = () => {
    resetProductFields();
    setShowAddModal(true);
  };

  return (
    <Container className="mt-5">
      <h1 className="text-center mb-4">Products Manager</h1>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      <Form>
        <Row className="mb-3">
          <Col md={4}>
            <Form.Group controlId="id">
              <Form.Label>Product ID</Form.Label>
              <Form.Control
                as="select"
                name="id"
                value={product.id}
                onChange={handleSelectProduct}
              >
                <option value={0}>Select Product</option>
                {productIds.map((p) => (
                  <option key={p.id} value={p.id}>
                    {p.id} - {p.name}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
          </Col>
          <Col md={8}>
            <Form.Group controlId="itemname">
              <Form.Label>Item Name</Form.Label>
              <Form.Control
                type="text"
                name="itemname"
                value={product.itemname}
                onChange={handleInputChange}
                disabled={product.id === 0}
              />
            </Form.Group>
          </Col>
        </Row>
        <Row className="mb-3">
          <Col md={6}>
            <Form.Group controlId="displayname">
              <Form.Label>Display Name</Form.Label>
              <Form.Control
                type="text"
                name="displayname"
                value={product.displayname}
                onChange={handleInputChange}
                disabled={product.id === 0}
              />
            </Form.Group>
          </Col>
          <Col md={3}>
            <Form.Group controlId="price">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                name="price"
                value={product.price}
                onChange={handleInputChange}
                min="0" // Prevent values below 0 via arrow buttons
                disabled={product.id === 0}
              />
            </Form.Group>
          </Col>
          <Col md={3}>
            <Form.Group controlId="stock">
              <Form.Label>Stock</Form.Label>
              <Form.Control
                type="number"
                name="stock"
                value={product.stock}
                onChange={handleInputChange}
                min="0" // Prevent values below 0 via arrow buttons
                disabled={product.id === 0}
              />
            </Form.Group>
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Group controlId="image_url">
              <Form.Label>Image URL</Form.Label>
              <div className="d-flex">
                <Form.Control
                  type="text"
                  name="image_url"
                  value={tempImageUrl}
                  onChange={(e) => setTempImageUrl(e.target.value)}
                  disabled={product.id === 0}
                />
                <Button
                  variant="primary"
                  onClick={handleImagePreview}
                  className="ms-2"
                  disabled={product.id === 0}
                >
                  Preview
                </Button>
              </div>
              {product.image_url && (
                <Card className="mt-3 position-relative">
                  <Card.Img
                    variant="top"
                    src={product.image_url}
                    alt="Product"
                    style={{ maxHeight: "200px", objectFit: "cover" }}
                  />
                  {product.image_url !== defaultImageUrl && (
                    <Button
                      variant="danger"
                      size="sm"
                      className="position-absolute top-0 end-0 m-2"
                      style={{
                        borderRadius: "50%",
                        width: "24px",
                        height: "24px",
                        padding: "0",
                        lineHeight: "1",
                      }}
                      onClick={handleResetImageUrl}
                    >
                      X
                    </Button>
                  )}
                </Card>
              )}
            </Form.Group>
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Group controlId="description">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="description"
                value={product.description}
                onChange={handleInputChange}
                disabled={product.id === 0}
              />
            </Form.Group>
          </Col>
        </Row>
        <Row>
          <Col>
            <Button variant="primary" className="w-100" onClick={handleAddProductClick}>
              Add Product
            </Button>
          </Col>
          <Col>
            <Button
              variant="success"
              className="w-100"
              onClick={handleUpdateProduct}
              disabled={product.id === 0}
            >
              Update Product
            </Button>
          </Col>
          <Col>
            <Button
              variant="danger"
              className="w-100"
              onClick={handleDeleteProduct}
              disabled={product.id === 0}
            >
              Delete Product
            </Button>
          </Col>
        </Row>
      </Form>

      {/* Add Product Modal */}
      <Modal show={showAddModal} onHide={() => setShowAddModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>Add New Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="newItemName" className="mb-3">
              <Form.Label>Item Name</Form.Label>
              <Form.Control
                type="text"
                name="itemname"
                value={product.itemname}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="newDisplayName" className="mb-3">
              <Form.Label>Display Name</Form.Label>
              <Form.Control
                type="text"
                name="displayname"
                value={product.displayname}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Row>
              <Col>
                <Form.Group controlId="newPrice" className="mb-3">
                  <Form.Label>Price</Form.Label>
                  <Form.Control
                    type="number"
                    name="price"
                    value={product.price}
                    onChange={handleInputChange}
                    min="0" // Prevent values below 0 via arrow buttons
                  />
                </Form.Group>
              </Col>
              <Col>
                <Form.Group controlId="newStock" className="mb-3">
                  <Form.Label>Stock</Form.Label>
                  <Form.Control
                    type="number"
                    name="stock"
                    value={product.stock}
                    onChange={handleInputChange}
                    min="0" // Prevent values below 0 via arrow buttons
                  />
                </Form.Group>
              </Col>
            </Row>
            <Form.Group controlId="newImageUrl" className="mb-3">
              <Form.Label>Image URL</Form.Label>
              <Form.Control
                type="text"
                name="image_url"
                value={tempImageUrl}
                onChange={(e) => setTempImageUrl(e.target.value)}
              />
              <Button variant="primary" onClick={handleImageUpload} className="mt-2">
                Upload
              </Button>
            </Form.Group>
            <Form.Group controlId="newDescription" className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="description"
                value={product.description}
                onChange={handleInputChange}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowAddModal(false)}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleAddProduct}>
            Add Product
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}

export default ProductsManager;
