import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from "./AuthProvider.jsx";
import { Form, Button, Container, Row, Col, Alert } from "react-bootstrap";

function UsersManager() {
  const { token } = useContext(AuthContext);
  const [users, setUsers] = useState([]);
  const [user, setUser] = useState({
    id: 0,
    username: "",
    email: "",
    phone_number: "",
    given_name: "",
    family_name: "",
    dob: "",
    address: "",
    account_level: 0,
    password: "",
    currentPassword: "",
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const fetchUsers = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/users", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await response.json();
      if (response.ok) {
        setUsers(data.sort((a, b) => a.id - b.id)); // Sort users by ID
      } else {
        setError(data.message);
      }
    } catch (err) {
      setError("Error fetching users.");
    }
  };

  useEffect(() => {
    fetchUsers();
  }, [token]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };

  const handleSelectUser = (e) => {
    const selectedId = parseInt(e.target.value, 10);
    const selectedUser = users.find((u) => u.id === selectedId) || {
      id: 0,
      username: "",
      email: "",
      phone_number: "",
      given_name: "",
      family_name: "",
      dob: "",
      address: "",
      account_level: 0,
      password: "",
      currentPassword: "",
    };
    setUser(selectedUser);
    setError("");
    setSuccess("");
  };

  const handleUpdateUser = async () => {
    if (!user.currentPassword) {
      setError("Current password is required to update user details.");
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/api/users/${user.id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(user),
      });
      const data = await response.json();
      if (response.ok) {
        setSuccess("User updated successfully!");
        fetchUsers();
      } else {
        setError(data.message);
      }
    } catch (err) {
      setError("Error updating user.");
    }
  };

  const isDisabled = user.id === 0;

  return (
    <Container className="mt-5">
      <h1 className="text-center mb-4">Users Manager</h1>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      <Form>
        <Row className="mb-3">
          <Col md={4}>
            <Form.Group controlId="id">
              <Form.Label>User ID</Form.Label>
              <Form.Control
                as="select"
                name="id"
                value={user.id}
                onChange={handleSelectUser}
              >
                <option value={0}>Select User</option>
                {users.map((u) => (
                  <option key={u.id} value={u.id}>
                    {u.id} - {u.username}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
          </Col>
          <Col md={8}>
            <Form.Group controlId="username">
              <Form.Label>Username</Form.Label>
              <Form.Control
                type="text"
                name="username"
                value={user.username}
                onChange={handleInputChange}
                disabled={isDisabled}
              />
            </Form.Group>
          </Col>
        </Row>
        <Row className="mb-3">
          <Col md={6}>
            <Form.Group controlId="email">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                name="email"
                value={user.email}
                onChange={handleInputChange}
                disabled={isDisabled}
              />
            </Form.Group>
          </Col>
          <Col md={6}>
            <Form.Group controlId="phone_number">
              <Form.Label>Phone Number</Form.Label>
              <Form.Control
                type="text"
                name="phone_number"
                value={user.phone_number}
                onChange={handleInputChange}
                disabled={isDisabled}
              />
            </Form.Group>
          </Col>
        </Row>
        <Row className="mb-3">
          <Col md={6}>
            <Form.Group controlId="given_name">
              <Form.Label>Given Name</Form.Label>
              <Form.Control
                type="text"
                name="given_name"
                value={user.given_name}
                onChange={handleInputChange}
                disabled={isDisabled}
              />
            </Form.Group>
          </Col>
          <Col md={6}>
            <Form.Group controlId="family_name">
              <Form.Label>Family Name</Form.Label>
              <Form.Control
                type="text"
                name="family_name"
                value={user.family_name}
                onChange={handleInputChange}
                disabled={isDisabled}
              />
            </Form.Group>
          </Col>
        </Row>
        <Row className="mb-3">
          <Col md={6}>
            <Form.Group controlId="dob">
              <Form.Label>Date of Birth</Form.Label>
              <Form.Control
                type="date"
                name="dob"
                value={user.dob}
                onChange={handleInputChange}
                disabled={isDisabled}
              />
            </Form.Group>
          </Col>
          <Col md={6}>
            <Form.Group controlId="address">
              <Form.Label>Address</Form.Label>
              <Form.Control
                type="text"
                name="address"
                value={user.address}
                onChange={handleInputChange}
                disabled={isDisabled}
              />
            </Form.Group>
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Group controlId="account_level">
              <Form.Label>Account Level</Form.Label>
              <Form.Control
                type="number"
                name="account_level"
                value={user.account_level}
                onChange={handleInputChange}
                min="0"
                disabled={isDisabled}
              />
            </Form.Group>
          </Col>
        </Row>
        <Row className="mb-3">
          <Col md={6}>
            <Form.Group controlId="password">
              <Form.Label>New Password</Form.Label>
              <Form.Control
                type="password"
                name="password"
                value={user.password}
                onChange={handleInputChange}
                disabled={isDisabled}
              />
            </Form.Group>
          </Col>
          <Col md={6}>
            <Form.Group controlId="currentPassword">
              <Form.Label>Current Password</Form.Label>
              <Form.Control
                type="password"
                name="currentPassword"
                value={user.currentPassword}
                onChange={handleInputChange}
                disabled={isDisabled}
              />
            </Form.Group>
          </Col>
        </Row>
        <Button
          variant="primary"
          className="w-100"
          onClick={handleUpdateUser}
          disabled={isDisabled}
        >
          Update User
        </Button>
      </Form>
    </Container>
  );
}

export default UsersManager;
