import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from "./AuthProvider.jsx";
import { Modal, Button, Card } from "react-bootstrap";

function Profile() {
  const { user, token, handleLogout } = useContext(AuthContext);
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
    currentPassword: "",
    profilePicture: "",
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [isUsernameAvailable, setIsUsernameAvailable] = useState(true);
  const [isEmailAvailable, setIsEmailAvailable] = useState(true);
  const [showPicturePopup, setShowPicturePopup] = useState(false);
  const [tempProfilePicture, setTempProfilePicture] = useState("");
  const defaultProfilePicture =
    "https://upload.wikimedia.org/wikipedia/commons/a/ac/Default_pfp.jpg?20200418092106";

  useEffect(() => {
    // Pre-fill the form with current user data
    if (user) {
      setFormData({
        username: user.username,
        email: user.email,
        password: "",
        currentPassword: "",
        profilePicture: user.profilePicture || defaultProfilePicture,
      });
    }
  }, [user]);

  const validateEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  const validatePassword = (password) =>
    /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/.test(password);

  const checkUsernameAvailability = async (username) => {
    if (username === user.username) return true; // Skip check if username is unchanged
    const response = await fetch("http://localhost:5000/api/check-username", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username }),
    });
    const data = await response.json();
    setIsUsernameAvailable(data.available);
    return data.available;
  };

  const checkEmailAvailability = async (email) => {
    if (email === user.email) return true; // Skip check if email is unchanged
    const response = await fetch("http://localhost:5000/api/check-email", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email }),
    });
    const data = await response.json();
    setIsEmailAvailable(data.available);
    return data.available;
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!formData.currentPassword) {
      setError("Current password is required to update your profile.");
      return;
    }

    if (formData.username.length < 4) {
      setError("Username must be at least 4 characters long.");
      return;
    }

    if (!isUsernameAvailable) {
      setError("Username is already taken.");
      return;
    }

    if (!validateEmail(formData.email)) {
      setError("Invalid email format.");
      return;
    }

    if (!isEmailAvailable) {
      setError("Email is already in use.");
      return;
    }

    if (formData.password && !validatePassword(formData.password)) {
      setError(
        "Password must be at least 8 characters long, include 1 uppercase letter, 1 lowercase letter, 1 number, and 1 special character."
      );
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/profile", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      if (response.ok) {
        setSuccess("Profile updated successfully!");
      } else {
        setError(data.message);
      }
    } catch {
      setError("An error occurred. Please try again.");
    }
  };

  const handleProfilePictureChange = () => {
    const img = new Image();
    img.src = tempProfilePicture;
    img.onload = () => {
      setFormData({ ...formData, profilePicture: tempProfilePicture });
      setShowPicturePopup(false);
      setError("");
    };
    img.onerror = () => {
      setError("Invalid image URL.");
    };
  };

  const resetProfilePicture = () => {
    setFormData({ ...formData, profilePicture: defaultProfilePicture });
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">Welcome, {user?.username}</h1>
      <div className="card mx-auto mt-4" style={{ maxWidth: "600px" }}>
        <div className="card-body text-center">
          <div className="position-relative">
            <img
              src={formData.profilePicture}
              alt="Profile"
              className="rounded-circle mb-3"
              style={{ width: "150px", height: "150px", objectFit: "cover" }}
            />
            {formData.profilePicture !== defaultProfilePicture && (
              <button
                className="btn btn-danger btn-sm position-absolute"
                style={{ top: "10px", right: "10px" }}
                onClick={resetProfilePicture}
              >
                X
              </button>
            )}
            <div
              className="position-absolute w-100 h-100 d-flex justify-content-center align-items-center"
              style={{
                top: 0,
                left: 0,
                backgroundColor: "rgba(0, 0, 0, 0.5)",
                opacity: 0,
                transition: "opacity 0.3s",
                cursor: "pointer",
              }}
              onMouseEnter={(e) => (e.currentTarget.style.opacity = 1)}
              onMouseLeave={(e) => (e.currentTarget.style.opacity = 0)}
              onClick={() => setShowPicturePopup(true)}
            >
              <span className="text-white">Change Picture</span>
            </div>
          </div>
          <form onSubmit={handleUpdate}>
            {error && <p className="text-danger">{error}</p>}
            {success && <p className="text-success">{success}</p>}
            <div className="mb-3">
              <input
                type="text"
                className="form-control"
                placeholder="New Username"
                value={formData.username}
                onChange={(e) => {
                  setFormData({ ...formData, username: e.target.value });
                  checkUsernameAvailability(e.target.value);
                }}
              />
              {!isUsernameAvailable && (
                <small className="text-danger">Username is already taken.</small>
              )}
            </div>
            <div className="mb-3">
              <input
                type="email"
                className="form-control"
                placeholder="New Email"
                value={formData.email}
                onChange={(e) => {
                  setFormData({ ...formData, email: e.target.value });
                  checkEmailAvailability(e.target.value);
                }}
              />
              {!isEmailAvailable && (
                <small className="text-danger">Email is already in use.</small>
              )}
            </div>
            <div className="mb-3">
              <input
                type="password"
                className="form-control"
                placeholder="New Password (optional)"
                value={formData.password}
                onChange={(e) =>
                  setFormData({ ...formData, password: e.target.value })
                }
              />
            </div>
            <div className="mb-3">
              <input
                type="password"
                className="form-control"
                placeholder="Current Password"
                value={formData.currentPassword}
                onChange={(e) =>
                  setFormData({ ...formData, currentPassword: e.target.value })
                }
              />
            </div>
            <button type="submit" className="btn btn-primary w-100">
              Update Profile
            </button>
          </form>
          <button
            className="btn btn-danger mt-3 w-100"
            onClick={handleLogout}
          >
            Logout
          </button>
        </div>
      </div>

      {/* Popup for changing profile picture */}
      <Modal show={showPicturePopup} onHide={() => setShowPicturePopup(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>Change Profile Picture</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Card className="text-center">
            <Card.Img
              variant="top"
              src={formData.profilePicture}
              alt="Current Profile"
              style={{ width: "100px", height: "100px", objectFit: "cover", margin: "0 auto" }}
              className="rounded-circle"
            />
            <Card.Body>
              <input
                type="text"
                className="form-control mb-3"
                placeholder="Paste image URL"
                value={tempProfilePicture}
                onChange={(e) => setTempProfilePicture(e.target.value)}
              />
              <Button variant="primary" onClick={handleProfilePictureChange}>
                Check
              </Button>
            </Card.Body>
          </Card>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default Profile;
