import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from "./AuthProvider.jsx";
import { Card, Button, Dropdown, DropdownButton, Container, Row, Col } from "react-bootstrap";

function Receipts() {
  const { token } = useContext(AuthContext);
  const [receipts, setReceipts] = useState([]);
  const [selectedReceipt, setSelectedReceipt] = useState(null);
  const [orderBy, setOrderBy] = useState("id");
  const [isDescending, setIsDescending] = useState(false);

  useEffect(() => {
    const fetchReceipts = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/receipts", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const data = await response.json();
        if (response.ok) {
          setReceipts(data);
        }
      } catch (err) {
        console.error("Error fetching receipts:", err);
      }
    };
    fetchReceipts();
  }, [token]);

  const sortedReceipts = [...receipts].sort((a, b) => {
    if (isDescending) {
      return b[orderBy] > a[orderBy] ? 1 : -1;
    }
    return a[orderBy] > b[orderBy] ? 1 : -1;
  });

  return (
    <Container className="mt-5">
      <h1 className="text-center mb-4">Receipts</h1>
      <div className="d-flex justify-content-between mb-3">
        <DropdownButton
          title={`Order By: ${orderBy}`}
          onSelect={(e) => setOrderBy(e)}
        >
          <Dropdown.Item eventKey="id">ID</Dropdown.Item>
          <Dropdown.Item eventKey="total_price">Total Amount</Dropdown.Item>
          <Dropdown.Item eventKey="seller_id">Seller ID</Dropdown.Item>
        </DropdownButton>
        <Button
          variant="light"
          onClick={() => setIsDescending(!isDescending)}
        >
          <img
            src="images/icons/sort/sort-32.png"
            alt="Sort"
            style={{ transform: isDescending ? "rotate(180deg)" : "none" }}
          />
        </Button>
      </div>
      <Row>
        {sortedReceipts.map((receipt) => (
          <Col md={4} key={receipt.id} className="mb-4">
            <Card>
              <Card.Body>
                <Card.Title>Receipt #{receipt.id}</Card.Title>
                <Card.Text>
                  <strong>Total:</strong> ${receipt.total_price}
                  <br />
                  <strong>Seller ID:</strong> {receipt.seller_id}
                </Card.Text>
                <Button
                  variant="primary"
                  onClick={() => setSelectedReceipt(receipt)}
                >
                  Expand
                </Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      {selectedReceipt && (
        <div className="mt-4">
          <h3>Receipt Details</h3>
          <p><strong>ID:</strong> {selectedReceipt.id}</p>
          <p><strong>Total:</strong> ${selectedReceipt.total_price}</p>
          <p><strong>Seller ID:</strong> {selectedReceipt.seller_id}</p>
          <p><strong>Items:</strong></p>
          <ul>
            {selectedReceipt.items.map((item, index) => (
              <li key={index}>
                {item.displayname} - {item.quantity} pcs @ ${item.price} each
              </li>
            ))}
          </ul>
        </div>
      )}
    </Container>
  );
}

export default Receipts;
