import React, { useState, useEffect } from "react";
import { jwtDecode } from "jwt-decode";
import { Container, Button, Navbar, Nav, Card } from "react-bootstrap"; // Import React-Bootstrap components
import Auth from "./Auth.jsx";
import Profile from "./Profile.jsx";
import Products from "./Products.jsx";
import Home from "./Home.jsx";

function App() {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [showAuth, setShowAuth] = useState(false);

  useEffect(() => {
    if (token) {
      try {
        const decodedToken = jwtDecode(token);

        if (decodedToken.exp * 1000 < Date.now()) {
          console.warn("Token has expired");
          handleLogout();
        } else {
          setUser({ id: decodedToken.id, username: decodedToken.username });
        }
      } catch (error) {
        console.error("Invalid token:", error);
        handleLogout();
      }
    }
  }, [token]);

  const handleLogin = (user, token) => {
    setUser(user);
    setToken(token);
    localStorage.setItem("token", token);
  };

  const handleLogout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem("token");
  };

  return (
    <Container className="mt-5">
      <Navbar bg="dark" variant="dark" expand="lg" className="mb-4">
        <Navbar.Brand href="#">IMS</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            {user && <Nav.Link href="#" onClick={handleLogout}>Logout</Nav.Link>}
          </Nav>
        </Navbar.Collapse>
      </Navbar>

      {!user ? (
        showAuth ? (
          <Auth onLogin={handleLogin} />
        ) : (
          <Card className="text-center">
            <Card.Body>
              <Card.Title>Welcome to IMS</Card.Title>
              <Card.Text>
                Manage your inventory efficiently and securely.
              </Card.Text>
              <Button variant="primary" onClick={() => setShowAuth(true)}>
                Login / Register
              </Button>
            </Card.Body>
          </Card>
        )
      ) : (
        <div>
          <h1 className="mb-4 text-center">Welcome, {user.username}</h1>
          <Profile token={token} />
          <Products token={token} />
        </div>
      )}
    </Container>
  );
}

export default App;
